mapper.select:
SELECT * FROM reply 
WHERE board_no =#{boardNo} 
ORDER BY reply_no DESC

mapper.delete:
DELETE FROM reply 
WHERE board_no=#{boardNo} 
ORDER BY reply_no > 0

mapper.select:
SELECT * FROM reply 
WHERE board_no=#{boardNo} 
ORDER BY reply_no DESC 
LIMIT #{criteria.pageStart}, #{criteria.countPerPage}
